import React, { useState, useEffect } from 'react';
import moment from 'moment';

const App = () => {
  const [data, setData] = useState([]);
  const [timezone, setTimezone] = useState('UTC+0');

  useEffect(() => {
    // Fetch data from the JSON file
    fetch('/data.json')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, [timezone]);

  const handleTimezoneChange = (e) => {
    setTimezone(e.target.value);
  };

  const handleCheckboxChange = (date, timeIndex) => {
    setData(prevData => {
      const newData = prevData.map(day => {
        if (day.date === date) {
          const newTimes = [...day.times];
          newTimes[timeIndex].isChecked = !newTimes[timeIndex].isChecked;
          return { ...day, times: newTimes };
        }
        return day;
      });
      return newData;
    });
  };

  return (
    <div>
      <label>Timezone:</label>
      <select value={timezone} onChange={handleTimezoneChange}>
        <option value="UTC+0">UTC+0</option>
        <option value="UTC+5">UTC+5</option>
      </select>

      <h2>Weekly Schedule</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Day</th>
            <th>Time</th>
            <th>Checkbox</th>
          </tr>
        </thead>
        <tbody>
          {data.map(day => (
            <React.Fragment key={day.date}>
              <tr>
                <td>{day.date}</td>
                <td>{day.dayOfWeek}</td>
              </tr>
              <tr>
                {day.times.map((timeSlot, index) => (
                  <React.Fragment key={`${day.date}-${timeSlot.time}`}>
                    <td>{timeSlot.time}</td>
                    <td>
                      <input
                        type="checkbox"
                        checked={timeSlot.isChecked}
                        onChange={() => handleCheckboxChange(day.date, index)}
                      />
                    </td>
                  </React.Fragment>
                ))}
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
