import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import moment from 'moment-timezone';
import Select from 'react-select';

import 'react-datepicker/dist/react-datepicker.css';

const WeeklySchedule = () => {
  const [selectedDate, setSelectedDate] = useState(moment());
  const [timezone, setTimezone] = useState('UTC');
  const [weeklySchedule, setWeeklySchedule] = useState([]);

  useEffect(() => {
    fetchData(calculateStartOfWeek(selectedDate), timezone);
  }, [selectedDate, timezone]);

  const fetchData = async (startDate, tz) => {
    try {
      // You can directly import the data.json file
      const response = await import('./data.json');
      const data = response.default; // Accessing the default export

      // Filter data for the current week
      const startOfWeek = startDate.startOf('week');
      const endOfWeek = startDate.endOf('week');
      const filteredData = data.filter((item) => moment(item.Date).isBetween(startOfWeek, endOfWeek, null, '[]'));

      setWeeklySchedule(filteredData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDateChange = (date) => {
    setSelectedDate(moment(date));
  };

  const handleTimezoneChange = (selectedOption) => {
    setTimezone(selectedOption.value);
  };

  const calculateStartOfWeek = (date) => {
    return moment(date).startOf('week');
  };

  const loadPreviousWeek = () => {
    setSelectedDate(selectedDate.clone().subtract(1, 'week'));
  };

  const loadNextWeek = () => {
    setSelectedDate(selectedDate.clone().add(1, 'week'));
  };

  return (
    <div>
      <h2>Weekly Schedule</h2>

      <div>
        <label>Date:</label>
        <DatePicker
          selected={selectedDate.toDate()}
          onChange={handleDateChange}
          dateFormat="yyyy-MM-dd"
        />
      </div>

      <div>
        <label>Timezone:</label>
        <Select
          options={[
            { value: 'UTC', label: 'UTC' },
            { value: 'America/New_York', label: 'America/New_York' },
            // Add more timezone options as needed
          ]}
          value={{ value: timezone, label: timezone }}
          onChange={handleTimezoneChange}
        />
      </div>

      {/* Display weekly schedule */}
      <div>
        <h3>Weekly Schedule</h3>
        <ul>
          {weeklySchedule.map((item) => (
            <li key={item.Id}>
              {item.Date} - {item.Time} - {item.Name}
            </li>
          ))}
        </ul>
      </div>

      {/* Add Previous & Next buttons to move between weeks */}
      <button onClick={loadPreviousWeek}>Previous Week</button>
      <button onClick={loadNextWeek}>Next Week</button>
    </div>
  );
};

export default WeeklySchedule;
